mapper.select:
SELECT COUNT(*) 
FROM mercenary_post 
WHERE member_code = #{member_code}

SELECT * FROM (
    SELECT ROWNUM rnum, tb.* FROM (
        SELECT 
            p.recruit_id, 
            p.category,        
            p.title, 
            p.view_count, 
            TO_CHAR(p.created_at, 'YYYY-MM-DD') AS created_at, 
            p.status,
            t.team_name,       t.emblem_image     FROM mercenary_post p
        LEFT JOIN team t ON p.team_code = t.team_code  WHERE p.member_code = #{member_code}
        ORDER BY p.recruit_id DESC
    ) tb WHERE ROWNUM <= #{offset} + #{size}
) WHERE rnum > #{offset}
