mapper.update:
UPDATE member_team
SET profile_image = #{profile_image}
WHERE team_code = #{team_code} 
  AND member_code = #{member_code}

mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT tm.member_code, m.member_name, m.member_id, tm.role_level, tm.position, tm.back_number, tm.profile_image, TO_CHAR(tm.reg_date, 'YYYY-MM-DD') AS join_date
FROM member_team tm JOIN member m ON tm.member_code = m.member_code WHERE tm.team_code = #{team_code}
AND (m.member_name LIKE '%' || #{keyword} || '%')
ORDER BY tm.role_level DESC, m.member_name ASC ) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

mapper.update:
UPDATE member_team


        role_level = #{role_level},


        position = #{position},


        back_number = #{back_number},


WHERE team_code = #{team_code} 
  AND member_code = #{member_code}

mapper.select:
SELECT schedule_code, subject, start_date, end_date, color, place
FROM team_schedule
WHERE team_code = #{team_code}
  AND start_date >= #{startStr} AND start_date <= #{endStr}

mapper.update:
UPDATE team_schedule
SET subject=#{subject}, start_date=#{start_date}, end_date=#{end_date}, color=#{color}, place=#{place}, memo=#{memo}
WHERE schedule_code=#{schedule_code}

mapper.select:
SELECT option_code, board_vote_code, content,
       /* 이 옵션의 득표수 */
       (SELECT COUNT(*) FROM team_vote_history WHERE option_code = o.option_code) AS option_count,
       /* 내가 체크했는지 여부 */
       NVL((SELECT 1 FROM team_vote_history WHERE option_code = o.option_code AND member_code = #{member_code}), 0) AS is_checked
FROM team_vote_option o
WHERE board_vote_code = #{board_vote_code}
ORDER BY option_code ASC

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT b.board_team_code, b.subject, m.user_name, 
		       TO_CHAR(b.reg_date, 'YYYY-MM-DD') as reg_date, b.hit_count
		FROM team_board b
		JOIN member m ON b.member_code = m.member_code
		WHERE b.team_code = #{team_code}
		ORDER BY b.board_team_code DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT role_level
FROM member_team
WHERE team_code = #{team_code} AND member_code = #{member_code}

SELECT NVL(COUNT(*), 0) 
FROM GALLERY 
WHERE TEAM_CODE = #{team_code}

SELECT FILE_ID, GALLERY_CODE, FILE_SERVER_NAME AS saveFilename
FROM FILE_GALLERY
WHERE GALLERY_CODE = #{gallery_code}

mapper.update:
UPDATE GALLERY
SET TITLE = #{title},
	CONTENT = #{content}

		, TITLE_IMAGE = #{title_image}

WHERE GALLERY_CODE = #{gallery_code}
